// src/App.js
import React, { useState } from 'react';
import TripForm from './components/TripForm';
import TripMap from './components/TripMap';
import LogSheet from './components/LogSheet';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [tripData, setTripData] = useState(null);

    const handleTripPlanned = (data) => {
        setTripData(data);
    };

    return (
        <div className="container">
            <h1 className="my-4 text-center">Truck Trip Planner</h1>
            <TripForm onTripPlanned={handleTripPlanned} />
            {tripData && (
                <>
                    <TripMap route={tripData.route} />
                    {tripData.log_sheets.map((log, idx) => (
                        <LogSheet key={idx} log={log} />
                    ))}
                </>
            )}
        </div>
    );
}

export default App;
