// src/components/TripMap.js
import React from 'react';
import { MapContainer, TileLayer, Polyline, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; // For custom icons
import 'leaflet/dist/leaflet.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const TripMap = ({ route, logSheets }) => {
    if (!route || !route.features || !route.features[0]) {
        return <div className="alert alert-warning mt-4">No route data available</div>;
    }

    const positions = route.features[0].geometry.coordinates.map(coord => [coord[1], coord[0]]);
    const startIcon = new L.Icon({
        iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
        iconSize: [25, 41],
        iconAnchor: [12, 41],
    });
    const stopIcon = new L.Icon({
        iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png',
        iconSize: [25, 41],
        iconAnchor: [12, 41],
    });

    // Identify key points (start, pickup, drop-off, rests)
    const keyPoints = [
        { pos: positions[0], label: 'Start (Current Location)' },
        { pos: positions[1], label: 'Pickup' },
        { pos: positions[positions.length - 1], label: 'Drop-off' },
    ];
    if (logSheets) {
        logSheets.forEach(sheet => {
            const restStart = sheet.log.indexOf('SB');
            if (restStart !== -1) {
                keyPoints.push({ pos: positions[Math.floor(positions.length * (restStart / 24))], label: `Rest Day ${sheet.day}` });
            }
        });
    }

    return (
        <div className="card map-container">
            <div className="card-header">Route Map</div>
            <div className="card-body">
                <MapContainer
                    center={positions[0]}
                    zoom={5}
                    style={{ height: '400px', width: '100%' }}
                    className="map"
                >
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='Â© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
                    />
                    <Polyline positions={positions} color="#007bff" weight={5} />
                    {keyPoints.map((point, index) => (
                        <Marker key={index} position={point.pos} icon={index === 0 ? startIcon : stopIcon}>
                            <Popup>{point.label}</Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>
        </div>
    );
};

export default TripMap;
