// src/components/TripForm.js
import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const TripForm = ({ onTripPlanned }) => {
    const [formData, setFormData] = useState({
        current_location: '',
        pickup_location: '',
        dropoff_location: '',
        current_cycle_used: 0
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/api/plan-trip/', formData);
            onTripPlanned(response.data);
        } catch (error) {
            console.error('Error planning trip:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="container mt-4">
            <div className="mb-3">
                <label className="form-label">Current Location</label>
                <input type="text" className="form-control" value={formData.current_location} onChange={(e) => setFormData({...formData, current_location: e.target.value})} required />
            </div>
            <div className="mb-3">
                <label className="form-label">Pickup Location</label>
                <input type="text" className="form-control" value={formData.pickup_location} onChange={(e) => setFormData({...formData, pickup_location: e.target.value})} required />
            </div>
            <div className="mb-3">
                <label className="form-label">Drop-off Location</label>
                <input type="text" className="form-control" value={formData.dropoff_location} onChange={(e) => setFormData({...formData, dropoff_location: e.target.value})} required />
            </div>
            <div className="mb-3">
                <label className="form-label">Current Cycle Used (Hrs)</label>
                <input type="number" className="form-control" value={formData.current_cycle_used} onChange={(e) => setFormData({...formData, current_cycle_used: parseFloat(e.target.value)})} min="0" max="70" required />
            </div>
            <button type="submit" className="btn btn-primary">Plan Trip</button>
        </form>
    );
};

export default TripForm;
