// src/components/LogSheet.js
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const LogSheet = ({ log }) => {
    const hours = Array.from({ length: 24 }, (_, i) => i);

    return (
        <div className="mt-4">
            <h3>Day {log.day} Log Sheet</h3>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>Status</th>
                        {hours.map(hour => <th key={hour}>{hour}:00</th>)}
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>Status</td>
                        {log.log.map((status, idx) => (
                            <td key={idx} className={
                                status === 'D' ? 'bg-success' :
                                status === 'ON' ? 'bg-warning' :
                                status === 'SB' ? 'bg-info' : 'bg-light'
                            }>
                                {status === 'D' ? 'Driving' : 
                                 status === 'ON' ? 'On Duty' : 
                                 status === 'SB' ? 'Sleeper' : 'Off'}
                            </td>
                        ))}
                    </tr>
                </tbody>
            </table>
        </div>
    );
};

export default LogSheet;
